import React, { useEffect } from "react";
import Chart from "chart.js/auto";
import { collection, onSnapshot } from "firebase/firestore";
import { db } from "../firebase";

const Dashboard = () => {
  useEffect(() => {
    const [lineData, setLineData] = useState([]);
    const [labels, setLabels] = useState([]);
    const chartRef = useRef(null);
    const pieChartRef = useRef(null);

    useEffect(() => {
      // Subscribe to Firestore data for line chart
      const unsub = onSnapshot(collection(db, "earnings"), (snapshot) => {
        const months = [];
        const earnings = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          months.push(data.month); // e.g. "Jan"
          earnings.push(data.amount); // e.g. 3000
        });
        setLabels(months);
        setLineData(earnings);
      });

      return () => unsub(); // cleanup Firestore listener
    }, []);

    // Line Chart
    if (ctxLine) {
      new Chart(ctxLine, {
        type: "line",
        data: {
          labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
          datasets: [
            {
              label: "Earnings",
              data: [0, 1000, 5000, 3000, 7000, 4000],
              borderColor: "#4e73df",
              backgroundColor: "rgba(78, 115, 223, 0.05)",
              fill: true,
              tension: 0.4,
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false, //this will take up the full width of the parent container
          plugins: {
            legend: { display: false },
          },
          scales: {
            y: { beginAtZero: true },
          },
        },
      });
    }

    // Pie Chart
    if (ctxPie) {
      new Chart(ctxPie, {
        type: "doughnut",
        data: {
          labels: ["Direct", "Social", "Referral"],
          datasets: [
            {
              data: [55, 30, 15],
              backgroundColor: ["#4e73df", "#1cc88a", "#36b9cc"],
              hoverBackgroundColor: ["#2e59d9", "#17a673", "#2c9faf"],
              hoverBorderColor: "rgba(234, 236, 244, 1)",
            },
          ],
        },
        options: {
          maintainAspectRatio: false,
          plugins: {
            legend: { position: "bottom" },
          },
          cutout: "75%",
        },
      });
    }

    // Cleanup on unmount
    return () => {
      if (lineChartInstance) lineChartInstance.destroy();
      if (pieChartInstance) pieChartInstance.destroy();
    };
  }, []);

  return (
    <div className="container-fluid">
      {/* <!-- Page Heading --> */}
      <div className="d-sm-flex align-items-center justify-content-between mb-4">
        <h1 className="h3 mb-0 text-gray-800">Dashboard</h1>
        <a
          href="#"
          className="d-none d-sm-inline-block btn btn-sm btn-primary shadow-sm"
        >
          <i className="fas fa-download fa-sm text-white-50"></i> Generate
          Report
        </a>
      </div>

      {/* <!-- Content Row --> */}
      <div className="row">
        {/* <!-- Users Card --> */}
        <div className="col-xl-4 col-md-6 mb-4">
          <div className="card border-left-primary shadow h-100 py-2">
            <div className="card-body">
              <div className="row align-items-center">
                <div className="col mr-2">
                  <div className="text-xs font-weight-bold text-primary text-uppercase mb-1">
                    Users
                  </div>
                  <div className="h5 mb-0 font-weight-bold text-gray-800">
                    200
                  </div>
                </div>
                <div className="col-auto">
                  <i className="fas fa-users fa-2x text-gray-300"></i>
                </div>
