import React from 'react';
import Sidebar from './Sidebar';
import Topbar from './Topbar';

const Layout = () => {
  return (
    <>
      <Topbar />
      <div className="main-container">
        <Sidebar />
        <Outlet /> {/* this renders the nested route content */}
      </div>
    </>
  );
};

export default Layout;
